import Search from '../Search/Search';
import { Button } from '@material-ui/core'
import { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import "./Banner.css";

const Banner = () => {

     const [showSearch,setShowSearch]=  useState(false);
     
     const history= useNavigate();

  return (
    <div className='banner'>
        <div className='banner_serach'>
              <Button className='banner_serchButton' onClick={()=>setShowSearch((prev) => !prev)}>
                   {showSearch ? "Hide":"Search Date"}
              </Button>
              {showSearch && <Search />}
        </div>
        <div className='banner_info'>
              <h1>Get out and stretch your imagination</h1>
              <h5>Plan a different kind of getaway to uncover the hidden games near you</h5>
              <Button variant='outlined' onClick={()=>history("/search")}> Explore Nearby</Button>

        </div>
    </div>
  )
}

export default Banner
